import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import Categories from '../components/Categories';
import InputSearch from '../components/InputSearch';
import { getProductsFromCategoryAndQuery } from '../services/api';

class Home extends Component {
  state = {
    search: '',
    products: [],
    isLoading: false,
  }

  setLoading = (bool) => {
    this.setState({ isLoading: bool });
  }

  handleCategory = async (paramID) => {
    const response = await getProductsFromCategoryAndQuery(paramID);
    const { results } = response;
    this.setState({
      products: results,
    });
  }

  handleOnChange = ({ target: { name, value } }) => {
    this.setState({
      [name]: value,
    });
  };

  handleSearch = async () => {
    this.setLoading(true);
    const { search } = this.state;
    const response = await getProductsFromCategoryAndQuery(undefined, search);
    const { results } = response;
    this.setState({
      products: results,
    });
    this.setLoading(false);
  };

  render() {
    const { products, search, isLoading } = this.state;
    return (
      <div>
        <Categories handleCategory={ this.handleCategory } />
        <section className="container">
          <div className="row justify-content-center mt-3">
            <InputSearch
              search={ search }
              handleOnChange={ this.handleOnChange }
              handleSearch={ this.handleSearch }
              isLoading={ isLoading }
            />
          </div>
        </section>
        <p
          data-testid="home-initial-message"
        >
          Digite algum termo de pesquisa ou escolha uma categoria.
        </p>
        <section className="container">
          {products.length === 0
            ? <p>Nenhum produto foi encontrado</p>
            : products
              .map((element) => (
                <Link
                  data-testid="product-detail-link"
                  to={ `/product/${element.id}/${searchBar}` }
                  key={ element.id }
                >
                  <div data-testid="product" className="div-category-element">
                    <img src={ element.thumbnail } alt={ element.title } />
                    <p>{ element.title }</p>
                    <h3>{ element.price }</h3>
                  </div>
                </Link>
              ))}
        </section>
      </div>
    );
  }
}

export default Home;
